mapper.select:
    
	SELECT pno, pname, pdetail, pdata, pregdate, pduedate, uno, lno
	FROM Project
	WHERE pno= #{pno}


SELECT COUNT(*) cnt
FROM ProjectMemberStat
WHERE pno = #{pno}

    
	SELECT COUNT(*)
	FROM Milestones
	WHERE pno=#{pno}

    
	SELECT COUNT(*)
	FROM Issues
	WHERE pno=#{pno}
