mapper.select:

select uno, uname, uphoneno, udepartment, uemail
from User
where uno=#{uno}

    
	SELECT U.uname, S.stat, C.CODE_NAME
	FROM ProjectMemberStat S, User U, CodeTable C
	WHERE S.uno = U.uno AND S.stat=C.CODE AND C.CODE_TYPE=010 AND S.pno=#{pno}


SELECT COUNT(*) cnt
FROM ProjectMemberStat
WHERE pno = #{pno}

    
	SELECT COUNT(*)
	FROM Milestones
	WHERE pno=#{pno}

    
	SELECT COUNT(*)
	FROM Issues
	WHERE pno=#{pno}
