mapper.select:
SELECT i.ino, ititle, idescription, iweight, istatement, i.mno, i.pno, i.uno, uname, mtitle, lno, ltitle, ldescription, lbgcolor FROM Issues i JOIN User u ON i.uno = u.uno JOIN Milestones m ON i.mno = m.mno JOIN ( SELECT l.lno, il.ino, ltitle, ldescription, lbgcolor FROM Label l JOIN IssueLabel il ON l.lno = il.lno ) l2 ON i.ino = l2.ino where i.pno=#{pno}
and istatement=#{istatement}
GROUP BY i.ino ORDER BY ino ASC

SELECT i.ino, ititle, idescription, iweight, istatement, i.mno, i.pno, i.uno
, uname
, mtitle
, lno, ltitle, ldescription, lbgcolor, i.pno
FROM Issues i
JOIN User u ON i.uno = u.uno
JOIN Milestones m ON i.mno = m.mno
JOIN ( SELECT l.lno, il.ino, ltitle, ldescription, lbgcolor FROM Label l JOIN IssueLabel il ON l.lno = il.lno ) l2 ON i.ino = l2.ino
where i.pno=#{pno}
and i.uno = #{userNo}
and istatement=#{istatement}
and i.mno=#{mno}
and iweight=#{weight}
and lno=#{lno}
ORDER BY ino ASC

mapper.update:
    
    UPDATE Issues SET
	    ititle = #{ititle},
		idescription = #{idescription},
		iweight = #{iweight},
		mno = #{mno}
	WHERE ino = #{ino}

    
    UPDATE Issues SET
	    istatement = #{istatement}
	WHERE ino = #{ino}
