mapper.select:
    
    select mno, mtitle, mduedate, mstatement FROM Milestones
	where pno=#{pno}
	
	
	and mstatement=#{mstatement}
	
	
	order by mno desc

    
    select mno, mtitle, mdescription, mduedate, mstatement, pno FROM Milestones
	where mno=#{mno}
	order by mno desc

mapper.update:
    
	   UPDATE Milestones SET
	  mtitle = #{mtitle}
	  ,mdescription = #{mdescription}
	  ,mduedate = #{mduedate}
	WHERE mno = #{mno}

    
	   UPDATE Milestones SET
	  mstatement = "001"
	WHERE mno = #{mno}

    
	   UPDATE Milestones SET
	  mstatement = "000"
	WHERE mno = #{mno}

mapper.select:
    
    SELECT COUNT(ino)
	FROM Issues
	WHERE mno=#{mno}
