mapper.select:
    
    select mno, mtitle, mduedate, mstatement FROM Milestones
	where pno=#{pno}
	
	
	and mstatement=#{mstatement}
	
	
	order by mno desc

mapper.update:
    
	   UPDATE Milestones SET
	  mtitle = #{mtitle}
	  ,mdescription = #{mdescription}
	  ,mduedate = #{mduedate}
	WHERE mno = #{mno}

    
	   UPDATE Milestones SET
	  mstatement = "001"
	WHERE mno = #{mno}

    
	   UPDATE Milestones SET
	  mstatement = "000"
	WHERE mno = #{mno}

mapper.select:
    
    SELECT COUNT(ino)
	FROM Issues
	WHERE mno=#{mno}

    
    SELECT ino, ititle, istatement, mno, pno, uno 
    FROM Issues
	WHERE mno = #{mno}

mapper.update:
    
	UPDATE Issues SET
	  istatement = #{istatement}
	
	
	  ,uno = #{uno}
	
	
	WHERE ino = #{ino}
